/**
 * GÃ¼tekriterium, das die Anzahl der nicht zugeteilten Studenten berechnet.
 */
public class NotAllocatedStudents implements QualityCriterion {

    private static final String DE_NAME = "Anzahl nicht zugeteilter Studenten";
    private static final String EN_NAME = "Number of not assigned students";

    /**
     * Diese Methode berechnet die Anzahl der nicht zugeteilten Studenten.
     * 
     * @return Die Wert der Anzahl nicht zugeteilter Studenten als String.
     */
    @Override
    public String calculate(Allocation allocation) {
        List<Team> teams = allocation.getTeams();
        List<Student> registeredStudents = allocation.getSemester().getStudents();

        int notAllocatedStudents = registeredStudents.size();
        for (int i = 0; i < teams.size(); i++) {
            notAllocatedStudents -= teams.get(i).getMembers().size();
        }
        return String.valueOf(notAllocatedStudents);
    }

    @Override
    public String getName(String local) {
        switch (local) {
        case "de":
            return DE_NAME;
        default:
            return EN_NAME;
        }
    }
}
